import { useState } from 'react';
import './App.css';
import './components/ColorfulMessage';
import ColorfulMessage from './components/ColorfulMessage';

function App() {
  const [num, setnum] = useState(0);
  const [showFlag,setShowFlag] = useState(true);
  const onClickButton = () =>{
    setnum(num + 1); 
  };
  const onClickShowFlag = ()=>{
    setShowFlag(showFlag ? false:true)
  }
  return (
    <div className="App">
      <h1>Hello React!</h1>
      <p>Let's practice</p>
      <ColorfulMessage color='blue' message='Are you happy? Really?'/>
      <button onClick={onClickButton}>Button</button>
      <p>{num}</p>
      <button onClick={onClickShowFlag}>ON/OFF</button>
      {showFlag && <p>^_^</p>}
    </div>
  );
}

export default App;
